import React, { Component } from 'react';
import './Decentralization.css'

import axios from 'axios'
const linkHouses = 'http://localhost:8000/houses/';
const linkAdmins = 'http://localhost:8000/admin/';
const linkStudents = "http://localhost:8000/students/";

class Decentralization extends Component {
    constructor(props) {
        super(props)
        this.state = {
            listHouse: [],
            listStudents: [],
            listHouseStudents: []
        }
    }

    changeRole = async (idaccount) => {
        const link = `${linkStudents}deleteExecutiveBoard`
        await axios.post(link, {idaccount})
    }

    getIdAndNameAllHouses = async () => {
        const link = `${linkHouses}GetIdAndNameAllHouses`
        let lsHouseStudents = []
        await axios.post(link)
            .then(house => {
                house.data.map((item) => {
                    let { idhouse, namehouse } = item
                    this.getAllStudent(idhouse, namehouse, lsHouseStudents)
                })
            })
        this.setState({ listHouseStudents: lsHouseStudents })
        console.log(this.state.listHouseStudents)
    }

    getAllStudent = async (idhouse, namehouse, lsHouseStudents) => {
        let link = `${linkStudents}GetDecentralizationByIdHouse/${idhouse}`
        axios.post(link)
            .then(async (result) => {
                let data = { idhouse: idhouse, namehouse: namehouse, listStudents: result.data }
                lsHouseStudents.push(data)
                for (let i = 0; i < lsHouseStudents.length - 1; i++) {
                    for (let j = i + 1; j < lsHouseStudents.length; j++) {
                        if (lsHouseStudents[j].idhouse < lsHouseStudents[i].idhouse) {
                            let temp = lsHouseStudents[i]
                            lsHouseStudents[i] = lsHouseStudents[j]
                            lsHouseStudents[j] = temp
                        }
                    }
                }
            })


    }



    renderInfoDecentralizationHouse = () => {
        return this.state.listHouseStudents.map(item => (
            <div className="col-12 col-md-6" key={item.idhouse}>
                    <div className="alert alert-success">
                        <strong>Nhà {item.idhouse} - {item.namehouse}</strong>
                    </div>
                    <div className="table-responsive">
                        <table className="table">
                            <thead>
                                <tr>
                                    <th width="12%">Ảnh</th>
                                    <th>Mã số</th>
                                    <th>Tên thánh - Họ Tên</th>
                                    <th>Chức vụ</th>
                                    <th>Xoá</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    item.listStudents.map((item) => (
                                        <tr key={item.idaccount}>
                                            <td>
                                            <img src={item.avatar.length !== null ? `../../uploads/accounts/${item.avatar}` : "../lib/images/hau.jpg"} className="img-fluid" alt="avatar" />
                                            </td>
                                            <td>{item.idaccount}</td>
                                            <td>{item.holyname + " " + item.lastname + " " + item.firstname}</td>
                                            <td>
                                                {item.role==='1A' || item.role==='1A2' ? 'Trưởng Nhà' :
                                                    item.role==='1B' || item.role === '1B2' ? 'Phó Nhà' : 'Quản Lý'
                                                }
                                            </td>
                                            <td>
                                                <button className="btn btn-sm btn-danger">
                                                    <i className="fas fa-trash-alt"></i>
                                                </button>
                                            </td>
                                        </tr>
                                    ))
                                }
                            </tbody>
                        </table>
                    </div>
            </div>
        ))
    }

    componentDidMount() {
        this.getIdAndNameAllHouses()
    }


    render() {
        return (
            <section className="main-content" >
                <div className="title-shared">
                    hệ thống phân quyền
                </div>
                <div className="container-fluid">
                    <div className="row d-flex flex-row-reverse">
                        <div className="col-12 col-sm-6 col-md-4">
                            <div className="form-group">
                                <select className="form-control form-control-sm">
                                    <option>Ban Điều Hành Lưu Xá</option>
                                    <option>Ban Truyền Thông</option>
                                </select>
                            </div>
                        </div>
                    </div>
                </div>
                <div className="container-fluid">
                    <div className="form-executive">
                        <div className="row">
                            <div className="col-12 text-right">
                                <div className="form-group">
                                    <button className="btn btn-sm btn-outline-success" onClick={this.showFormAddHouse}>
                                        <i className="fas fa-plus"></i> &nbsp; Thêm ban điều hành lưu xá
                                    </button>
                                </div>
                            </div>
                            {this.renderInfoDecentralizationHouse()}
                        </div>
                    </div>
                    <div className="form-communication">

                    </div>
                </div>
            </section>
        );
    }
}

export default Decentralization;
