import React, { Component } from 'react'
import axios from 'axios'

class BookingTickets extends Component {
  constructor(props) {
    super(props)
    this.state = {
      idBooking: '',
      idChair: '',
      idCar: '',
      dayGo: '',
      timeGo: ''
    }

  }
  onChange = (e) => {
    this.setState({ idCar: e.target.value })
    console.log(this.state.idCar)
  }

  render() {
    return (
      <div className="container">
        <h2>Đặt vé</h2>
        <form action="/datve" method="POST">
          <label for="my-input">Số xe</label>
          <input id="my-input" className="form-control" type="text" name="idCar" onChange={this.onChange} value={this.state.idCar} />
          <label for="my-input">Số ghế</label>
          <input id="my-input" className="form-control" type="text" name="idChair" />
          <label for="my-input">Ngày đi</label>
          <input id="my-input" className="form-control" type="text" name="dayGo" />
          <label for="my-input">Giờ đi</label>
          <input id="my-input" className="form-control" type="text" name="timeGo" />
          <br />
          <input type='submit' className="btn btn-primary" value="Submit" />
        </form>
      </div>
    )
  }
}

export default BookingTickets
