import React, { Component } from 'react';
import axios from 'axios'
import './searchBus.css'

class SearchBus extends Component {
  constructor(props) {
    super(props)
    this.state = {
      listBus: [],
      listAutoMarket: []
    }
  }

  filterBus = (value) => {
    let data = value + ''
    const link = 'http://localhost:8000/filterBus'
    axios.post(link, { data: data })
      .then(result => {
        this.setState({ listBus: result.data });
      })
  }
  onChange = (e) => {
    let value = e.target.value
    let name = e.target.name
    if (name === 'keySearch') {
      this.filterBus(value)
    }
  }

  getAllCar = () => {
    let link = 'http://localhost:8000/getAllBus'
    axios
      .post(link)
      .then(result => {
        this.setState({ listBus: result.data });
      })
  }

  getAllAutoMarket = () => {
    let link = 'http://localhost:8000/getAllAutoMarket'
    axios
      .post(link)
        .then(result => {
          this.setState({listAutoMarket: result.data});
        })
  }

  renderBus = () => {
    return this.state.listBus.map((item, key) => (
      <div>
        <div className='row Bus'>
          <div key = {key}>
            <div className='col-sm-5 float-sm-left' >
              <img src={`../lib/images/${item.img}`} className="img-fluid" alt="avatar" />
            </div>
            <div className='col-sm-7 float-sm-left '>
              <div><strong className='nameBus'>{item.name}</strong></div>
              <div className='descriptionBus'>{item.description}</div>
            </div>
          </div>
        </div>
        <br />
      </div>
    ))
    // console.log('đã vào')
  }

  componentDidMount() {
    this.getAllCar()
    this.getAllAutoMarket()
  }

  renderAutoMarker = () => {
    return (
      <div>
        <select className="form-control form-control-sm col-sm-3">
          <option>Hãng</option>

        </select>
      </div>
    )
  }



  render() {
    return (
      <div>
        <div className='container'>
          <h1>Search Car</h1>
          <div className='row'>
            <input className='col-sm-11' type='text' name='keySearch' onChange={this.onChange}></input>
            <button className='btn btn-success col-sm-1'><i className="fas fa-search"></i></button>
          </div>
          <br />
          <div className='row select'>
            <div className='col-sm-3'></div>
            {this.renderAutoMarker()}
            <select className="form-control form-control-sm col-sm-3">
              <option> Số ghế </option>
              <option>Ban Điều Hành Lưu Xá</option>
              <option>Ban Truyền Thông</option>
            </select>
          </div>
          <br />
          <hr />
          <div className='listBus'>
            {this.renderBus()}
          </div>


        </div>
      </div>
    )
  }
}


export default SearchBus
