import React, { Component } from 'react';
import axios from 'axios'
import './backupDatabase.css'
const Papa = require('papaparse')
var FileSaver = require('file-saver')

class BackupDatabase extends Component {
  constructor(props) {
    super(props)
    this.state = {
      filecsv : []
    }
  }

onChange = () => {
  let link = 'http://localhost:8000/getAllDatabase'
  axios.post(link).then(result => {
    // console.log(result)
    let csv = Papa.unparse(result)
    data: result
  })
  console.log(csv)
}

  render() {
    return (
      <div className='container'>
        <h1>Backup Database</h1>
        <hr />
        <div>
          <div className = 'button' className = 'row'>
            <div className = 'col-sm-4'></div>
            <button className='btn btn-success col-sm-2'> Import Database </button>
            <div className = 'col-sm-1'></div>
            <button className='btn btn-success col-sm-2' onClick = {this.onChange}> Export Database </button>
            <div className = 'col-sm-3'></div>
          </div>

        </div>
      </div>
    )
  }
}

export default BackupDatabase
