import React, { Component } from 'react';
import axios from 'axios'
import './toDoList.css'

class toDoList extends Component {
  constructor(props) {
    super(props)
    this.state = {
      task: '',
      todolist: []
    }
  }

  onChange = (e) => {
    this.setState({task: e.target.value});
  }

  submit = () => {
    const link = 'http://localhost:8000/'
    axios.post(link,{task: this.state.task})
    .then(result => {
      this.setState({toDoList: result.data});
    })
  }

  renderTodoList = () => {
    return this.state.todolist.map(item => {
      <div>{item.name}</div>
    })
  }
  render() {
    return (
      <div className = 'container'>
          <h1> TO DO LIST</h1>
          <div>
            <h4>Task:</h4>  <input type="text" className="form-control"  onChange = {this.onChange} value = {this.state.task}/>
          </div>
          <br/>
          <button className='btn btn-success btn-block' onClick = {this.submit}> ThÃªm </button>
          {this.renderTodoList()}
      </div>
    )
  }
}


export default toDoList
