import React, { Component } from 'react'
import axios from 'axios'

class BookingTickets extends Component {
  constructor(props) {
    super(props)
    this.state = {
      idBooking: '',
      idChair: '',
      idCar: "",
      dayGo: '',
      timeGo: ''
    }

  }
  onChange = (e) => {
    this.setState({[e.target.name] : e.target.value})
    console.log(this.state.idCar)
    console.log(this.state.idChair)
  }

  handleSubmit = () => {
    let link = 'http://localhost:8000/bookingTickets'
    console.log(link)
    let data = {idCar: this.state.idCar, idChair: this.state.idChair, dayGo: this.state.dayGo, timeGo: this.state.timeGo}
    axios
    .post(link, data)
      .then(res => {

      })

  }

  render() {
    return (
      <div className="container">
        <h2>Đặt vé</h2>
          <label for="my-input">Số xe</label>
          <input id="my-input" className="form-control" type="text" name="idCar" onChange={this.onChange} />
          <label for="my-input">Số ghế</label>
          <input id="my-input" className="form-control" type="text" name="idChair" onChange={this.onChange} />
          <label for="my-input">Ngày đi</label>
          <input id="my-input" className="form-control" type="text" name="dayGo" onChange={this.onChange}/>
          <label for="my-input">Giờ đi</label>
          <input id="my-input" className="form-control" type="text" name="timeGo" onChange={this.onChange}/>
          <br />
          <button className='btn btn-primary' onClick = {this.handleSubmit}>Submit</button>
      </div>
    )
  }
}

export default BookingTickets
