import React, { Component } from 'react';
import axios from 'axios'
import './backupDatabase.css'
const Papa = require('papaparse')
var FileSaver = require('file-saver')

class BackupDatabase extends Component {
  constructor(props) {
    super(props)
    this.state = {
      lisAutoMarker : [],
      listBus: [],
      lisSeat: []
    }
  }

onChange = () => {
  let link = 'http://localhost:8000/getAllDatabase'
  axios.post(link).then(result => {
    config = {
      delimiter: "",	// auto-detect
      newline: "",	// auto-detect
      quoteChar: '"',
      escapeChar: '"',
      header: false,
      transformHeader: undefined,
      dynamicTyping: false,
      preview: 0,
      encoding: "",
      worker: false,
      comments: false,
      step: undefined,
      complete: undefined,
      error: undefined,
      download: true,
      downloadRequestHeaders: undefined,
      skipEmptyLines: false,
      chunk: undefined,
      fastMode: undefined,
      beforeFirstChunk: undefined,
      withCredentials: undefined,
      transform: undefined,
      delimitersToGuess: [',', '\t', '|', ';', Papa.RECORD_SEP, Papa.UNIT_SEP]
    }
    this.setState({lisAutoMarker: result.data.listAutoMarker})
    this.setState({lisSeat: result.data.listSeat})
    this.setState({listBus: result.data.listBus})
    let lisAutoMarkerCsv = Papa.unparse(this.state.lisAutoMarker, [, config])
    let lisBusCsv = Papa.unparse(this.state.listBus, [, config])
    let listSeatCsv = Papa.unparse(this.state.lisSeat, [, config])

})
}

  render() {
    return (
      <div className='container'>
        <h1>Backup Database</h1>
        <hr />
        <div>
          <div className = 'button' className = 'row'>
            <div className = 'col-sm-4'></div>
            <button className='btn btn-success col-sm-2'> Import Database </button>
            <div className = 'col-sm-1'></div>
            <button className='btn btn-success col-sm-2' onClick = {this.onChange}> Export Database </button>
            <div className = 'col-sm-3'></div>
          </div>

        </div>
      </div>
    )
  }
}

export default BackupDatabase
