import React, { Component } from 'react';
import axios from 'axios'
import './toDoList.css'

class toDoList extends Component {
  constructor(props) {
    super(props)
    this.state = {
      task: '',
      todolist: []
    }
  }

  onChange = (e) => {
    this.setState({ task: e.target.value });
  }

  submit = () => {
    let link = 'http://localhost:8000/addToDo'
    axios.post(link, { task: this.state.task })
      .then(result => {
        console.log(result.data)
        this.setState({ todolist: result.data });
      })
  }


  renderToDoList = () => {
    let link = 'http://localhost:8000/listToDo'
    axios.post(link)
    .then(result => {
      this.setState({todolist: result.data});
    })
    return this.state.todolist.map((item, key) => (
      <div className='row' key = {key} >
      <strong className = 'col-sm-10'>{item.name}</strong> 
      <button className = 'btn btn-danger'><i className="fas fa-check"></i></button>
      <hr/>
      </div>

    ))
  }

  render() {
    return (
      <div className='container'>
        <h1> TO DO LIST</h1>
        <div>
          <h4>Task:</h4>  <input type="text" className="form-control" onChange={this.onChange} value={this.state.task} />
        </div>
        <br />
        <button className='btn btn-success btn-block' onClick={this.submit}> ThÃªm </button>
        {this.renderToDoList()}
      </div>
    )
  }
}


export default toDoList
