import React, { Component } from 'react';
import axios from 'axios'
import './searchBus.css'

class SearchBus extends Component {
  constructor(props) {
    super(props)
    this.state = {
      listBus: []
    }
  }

  onChange = (e) => {
    this.setState({ task: e.target.value });
  }

  getAllCar = () => {
    let link = 'http://localhost:8000/getAllBus'
    axios
      .post(link)
      .then(result => {
        this.setState({ listBus: result.data });
      })
  }

  renderBus = () => {
    return this.state.listBus.map((item, key) => (
      <div className='row'>
        <div key={key}>
          <div className='col-sm-5' >
            <img src={`../lib/images/${item.img}`} className="img-fluid" alt="avatar"  width='300'/>
          </div>
          <div className='col-sm-7'>
            <div><strong>{item.name}</strong></div>
            <div>{item.description}</div>
          </div>
        </div>
      </div>
    ))
    // console.log('đã vào')
  }

  componentDidMount() {
    this.getAllCar()
  }




  render() {
    return (
      <div>
        <div className='container'>
          <h1>Search Car</h1>
          <div className='row'>
            <input className='col-sm-11' type='text'></input>
            <button className='btn btn-success col-sm-1'><i className="fas fa-search"></i></button>
          </div>
          <br />
          <div className='row select'>
            <div className='col-sm-3'></div>
            <select className="form-control form-control-sm col-sm-3">
              <option>Hãng</option>
              <option>Ban Điều Hành Lưu Xá</option>
              <option>Ban Truyền Thông</option>
            </select>

            <select className="form-control form-control-sm col-sm-3">
              <option> Số ghế </option>
              <option>Ban Điều Hành Lưu Xá</option>
              <option>Ban Truyền Thông</option>
            </select>
          </div>
          <div className='row'>
            {this.renderBus()}
          </div>
        </div>
      </div>
    )
  }
}


export default SearchBus
